package homework;

public class HW1_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 12, y = 6;
		int sum = 0, product = 0;
		
		sum = x + y;
		
		product = x * y;
		
		System.out.println(x + " + " + y + " = " + sum);
		System.out.printf("%d + %d = %d\n", x, y, sum );
		System.out.println(x + " x " + y + " = " + product);
	}

}
